import React, { Component } from 'react';
import geojson from './geojson.json';
import { geoMercator, geoPath } from 'd3-geo';
import { select } from 'd3-selection';

// Component for the filter box
const FilterBox = () => {
  const filterBoxStyle = {
    position: 'absolute',
    bottom: '20px',
    right: '20px',
    padding: '10px',
    backgroundColor: 'rgba(255, 255, 255, 0.7)',
    borderRadius: '5px',
  };

  return <div style={filterBoxStyle}>Filter Box</div>;
};

class Map extends Component {
  constructor(props) {
    super(props);
    this.state = {
      expandedState: null,
    };
  }

  handleStateClick = (name) => {
    if (this.state.expandedState === name) {
      this.setState({ expandedState: null });
    } else {
      this.setState({ expandedState: name });
    }
  };

  render() {
    const filteredFeatures = geojson.features.filter(
      (feature) =>
        feature.properties.NAME !== 'Alaska' &&
        feature.properties.NAME !== 'Hawaii'
    );

    const width = 1500;
    const height = width * 0.7;
    const projection = geoMercator().fitSize([width, height], {
      type: 'FeatureCollection',
      features: filteredFeatures,
    });
    const path = geoPath().projection(projection);

    const svgStyle = {
      display: 'block',
      margin: 'auto',
    };

    const containerStyle = {
      position: 'relative',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
    };

    return (
      <div style={containerStyle}>
        <svg width={width} height={height} style={svgStyle}>
          <g className="geojson-layer">
            {filteredFeatures.map((d) => (
              <path
                key={d.properties.NAME}
                d={path(d)}
                fill={this.state.expandedState === d.properties.NAME ? '#FF5733' : '#eee'}
                stroke="#0e1724"
                strokeWidth="1"
                strokeOpacity="0.5"
                onClick={() => this.handleStateClick(d.properties.NAME)}
              />
            ))}
          </g>
        </svg>
        <FilterBox /> {/* Adding the filter box component */}
      </div>
    );
  }
}

export default Map;
